svg.script:
function scale(max, min, num) {return (100 * (num - min) / (max - min)) || 0;}
function draw() {
  var elem = document.getElementsByTagName("svg")[0],
      parts = location.search.substr(1).split(",")
              .map(function (n) {
                return parseFloat(n, 10) || 0;
              }),
      max = Math.max.apply(null, parts),
      min = Math.min.apply(null, parts);
  parts = parts.map(scale.bind(null, max, min));
  var x, y, height, width = 100 / parts.length;
  for (var i=0; i < parts.length; i++) {
    var rect = document.createElementNS("http://www.w3.org/2000/svg", "rect");
    x = 100 * (i / parts.length);
    y = 100 - parts[i];
    height = parts[i];
    rect.setAttribute("x", x + "%");
    rect.setAttribute("y", y + "%");
    rect.setAttribute("width", width + "%");
    rect.setAttribute("height", height + "%");
    rect.setAttribute("fill", "rgba(0,0,0,1)");
    rect.setAttribute("stroke", "rgba(0,0,0,1)");
    elem.appendChild(rect);
  }
}
