svg.script:
function scale(max, min, num) {return (100 * (num - min) / (max - min)) || 0;}
function limit(max, min, num) {return num > max ? max : num < min ? min : num;}
var timeout;
function clear() {
  clearTimeout(timeout);
  timeout = setTimeout(function () {
    var elem = document.getElementsByTagName("svg")[0];
    while (elem.firstChild) { elem.removeChild(elem.firstChild); }
    draw();
  }, 250);
}
function draw() {
  var elem = document.getElementsByTagName("svg")[0],
      parts = location.search.substr(1).split(",")
              .map(function (n) {
                return parseFloat(n, 10) || 0;
              }),
      max = Math.max.apply(null, parts),
      min = Math.min.apply(null, parts);
  parts = parts.map(scale.bind(null, max, min));
  var div = 100 / parts.length,
      divInPx = elem.clientWidth / parts.length,
      heightR = elem.clientHeight / 2,
      maxR = divInPx > heightR ? heightR : divInPx;
      cy = 50;
  for (var i=0; i < parts.length; i++) {
    var circle = document.createElementNS("http://www.w3.org/2000/svg", "circle");
    var cx = (i * div) + (div / 2),
        r = maxR * (parts[i] / 100);
    circle.setAttribute("cx", cx + "%");
    circle.setAttribute("cy", cy + "%");
    circle.setAttribute("r", r + "px");
    circle.setAttribute("fill", "rgba(0,0,0,.5)");
    circle.setAttribute("stroke", "rgba(0,0,0,.2)");
    elem.appendChild(circle);
  }
}
