svg.script:
function scale(max, min, num) {return (100 * (num - min) / (max - min)) || 0;}
function draw() {
  var elem = document.getElementsByTagName("svg")[0],
      parts = location.search.substr(1).split(",")
              .map(function (n) {
                return parseFloat(n, 10) || 0;
              }),
      max = Math.max.apply(null, parts),
      min = Math.min.apply(null, parts);
  parts = parts.map(scale.bind(null, max, min));
  var div = 100 / parts.length,
      cy = 50;
  for (var i=0; i < parts.length; i++) {
    var rect = document.createElementNS("http://www.w3.org/2000/svg", "circle");
    var cx = (i * div) + (div / 2),
        r = parts[i] / parts.length;
    console.log(cx, parts[i], r);
    rect.setAttribute("cx", cx + "%");
    rect.setAttribute("cy", cy + "%");
    rect.setAttribute("r", r + "%");
    rect.setAttribute("fill", "rgba(0,0,0,.5)");
    rect.setAttribute("stroke", "rgba(0,0,0,.2)");
    elem.appendChild(rect);
  }
}
